import React, { useState, useEffect } from "react";
import { View } from "react-native";
import * as ImagePicker from "expo-image-picker";

//import components

import ImageInputList from "../components/ImageInputList";

//import styles and assets
import styled from "styled-components";
import { H, Sub1, P } from "../config/Typography";
import colors from "../config/colors";
import * as Button from "../components/Button";

//import redux
import { connect } from "react-redux";
import { setImages } from "../store/host";

const HostingStep2 = (props) => {
  const [imageUris, setImageUris] = useState([]);

  const handleAdd = (uri) => {
    setImageUris([...imageUris, uri]);
  };

  const handleRemove = (uri) => {
    setImageUris(imageUris.filter((imageUri) => imageUri !== uri));
  };

  const onNavigate = () => {
    props.setImages(imageUris);
    props.navigation.navigate("HostingStep3");
  };

  return (
    <Container>
      <Main>
        <H>Add a photo of your listing</H>
        <Sub1 colors={colors.darkgray}>
        Make your accommodation stand out with great photos
        </Sub1>

        <Step style={{ paddingTop: 10 }}>
          <ImageInputList
            imageUris={imageUris}
            onAddImage={handleAdd}
            onRemoveImage={handleRemove}
          />
        </Step>
        <Step>
          {/* <Image
            source={{ uri: imageUri }}
            style={{ width: 200, height: 200 }}
          /> */}
        </Step>
      </Main>
      <Next>
        <Left></Left>
        <BtnContainer>
          <Button.BtnContain
            label="next"
            size="small"
            color={colors.green}
            onPress={() => onNavigate()}
          />
        </BtnContainer>
      </Next>
    </Container>
  );
};

const Container = styled.View`
  flex: 1;
  background-color: white;
`;

const Main = styled.ScrollView`
  flex: 1;
  padding: 24px;
`;

const Next = styled.View`
  padding: 20px;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  border-top-width: 1px;
  border-top-color: ${colors.faintgray};
  background-color: white;
`;

const Left = styled.View``;

const BtnContainer = styled.View`
  width: 30%;
`;

const Step = styled.View`
  margin: 20px 0;
`;

const InputWrapper = styled.View`
  margin: 15px 0 10px 0;
`;

const Flex = styled.View`
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  margin: 15px 0 10px 0;
`;

export default connect(null, { setImages })(HostingStep2);
